example.screen:
options {
        directory "/var/lib/named";
        forwarders { 10.11.12.13; 10.11.12.14; };
        listen-on { 127.0.0.1; &dnsip;; };
        allow-query { 127/8; 192.168/16 };
        notify no;
};

options {
        directory "/var/lib/named";
        forwarders { 10.0.0.1; };
        notify no;
};

zone "localhost" in {
       type master;
       file "localhost.zone";
};

zone "0.0.127.in-addr.arpa" in {
        type master;
        file "127.0.0.zone";
};

zone "." in {
        type hint;
        file "root.hint";
};

zone "example.com" in {
      type master;
      file "example.com.zone";
      notify no;
};

zone "&exampledomain1;" in {
      type slave;
      file "slave/&exampledomain1;.zone";
      masters { 10.0.0.1; }; 
};

$TTL 2D 
&exampledomain;. IN SOA      dns  root.&exampledomain;. ( 
             2003072441  ; シリアル番号 
             1D          ; 更新間隔 
             2H          ; 再試行間隔 
             1W          ; 有効期限 
             2D )        ; ネガティブキャッシュ TTL 

             IN NS       dns 
             IN MX       10 mail dns 
gate         IN A        &gateip; 
             IN A        10.0.0.1
dns          IN A        &dnsip;
mail         IN A        &mailip;
&wsI;      IN A        &wsIip;
&wsII;        IN A        &wsIIip;
&wsIII;       IN A        &wsIIIip;
&wsIV;      IN A        &wsIVip;
ntp          IN CNAME    dns 
dns6         IN A6  0    &dnsipv6;
